import React, { useState } from "react";

const AddItemForm = () => {
  const [enteredImage, setEnteredImage] = useState("");
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredDescription, setEnteredDescription] = useState("");
  const [enteredAvailableSizes, setEnteredAvailableSizes] = useState("");
  const [enteredPrice, setEnteredPrice] = useState("");

  const setObjectData = (event) => {
    if(event.target.name==="img")
    setEnteredImage(event.target.value);
    if(event.target.name==="title")
    setEnteredTitle(event.target.value);
    if(event.target.name==="desc")
    setEnteredDescription(event.target.value);
    if(event.target.name==="availableSize")
    setEnteredAvailableSizes(event.target.value);
    if(event.target.name==="price")
    setEnteredPrice(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const newData = {
      image: enteredImage,
      title: enteredTitle,
      description: enteredDescription,
      availableSizes: enteredAvailableSizes,
      price: enteredPrice,
    };
    console.log(newData);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div>
          <label for="img">Select image:</label>
          <input type="file" id="img" name="img" accept="image/*"  onChange={setObjectData}></input> 
        </div>

        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={enteredTitle} name="title" onChange={setObjectData} />
        </div>

        <div className="new-expense__control">
          <label>Describtion</label>
          <input type="text" value={enteredDescription} name="desc" onChange={setObjectData} />
        </div>

        <div className="new-expense__control">
          <label>availableSize</label>
          <input type="text" value={enteredAvailableSizes} name="availableSize" onChange={setObjectData} />
        </div>

        <div className="new-expense__control">
          <label>price</label>
          <input type="number" value={enteredPrice} name="price" onChange={setObjectData} />
        </div>

        <div className="new-expense__actions">
          <button type="submit">Submit</button>
        </div>
      </div>
    </form>
  );
};

export default AddItemForm;
