import React, { useState } from "react";
import "./index.css";

const AddItemForm = (props) => {
  const [enteredImage, setEnteredImage] = useState("");
  const [isValid, setIsValid] = useState(true);
  const [form, setForm] = useState({
    title: "",
    desc: "",
    size: "",
    price: "",
  });

  const setObjectData = (event) => {
    if (event.target.name === "img") {
      var fread = new FileReader();
      fread.readAsDataURL(event.target.files[0]);
      fread.onloadend = function (event) {
        setEnteredImage(event.target.result);
      };
      setEnteredImage(event.target.files[0]);
    } else {
      setForm((prev) => {
        return {
          ...prev,
          [event.target.name]: [event.target.value],
        };
      });
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();
    if(enteredImage.trim().length === 0);
    if(form.title.trim().length === 0);
    if(form.desc.trim().length === 0);
    if(form.size.trim().length === 0);
    if(form.price.trim().length === 0);
    {
      return;
    }


    props.setdatafunc(
      enteredImage,
      form.title,
      form.desc,
      form.size,
      form.price
    );
    props.submitflagfunc(true);
  };

  return (
    <>
      <form
        onSubmit={submitHandler}
        className="new-expense__controls new-expense"
      >
        <div className="new-expense__controls">
          <div>
            <label style={{ color: !isValid ? "red" : "black" }} htmlFor="img">
              Select image:
            </label>
            <input
              style={{
                borderColor: !isValid ? "red" : "black",
                background: !isValid ? "salmon" : "transparent",
              }}
              type="file"
              id="img"
              name="img"
              accept="image/*"
              onChange={setObjectData}
            ></input>
          </div>

          <div className="new-expense__control">
            <label style={{ color: !isValid ? "red" : "black" }}>Title</label>
            <input
              style={{
                borderColor: !isValid ? "red" : "black",
                background: !isValid ? "salmon" : "transparent",
              }}
              type="text"
              value={form.title}
              name="title"
              onChange={setObjectData}
            />
          </div>

          <div className="new-expense__control">
            <label style={{ color: !isValid ? "red" : "black" }}>Describtion</label>
            <input
            style={{
                borderColor: !isValid ? "red" : "black",
                background: !isValid ? "salmon" : "transparent",
              }}
              type="text"
              value={form.desc}
              name="desc"
              onChange={setObjectData}
            />
          </div>

          <div className="new-expense__control">
            <label>availableSize</label>
            <input
              type="text"
              value={form.size}
              name="size"
              onChange={setObjectData}
            />
          </div>

          <div className="new-expense__control">
            <label>price</label>
            <input
              type="number"
              value={form.price}
              name="price"
              onChange={setObjectData}
            />
          </div>

          <div className="new-expense__actions">
            <button type="submit">Submit</button>
          </div>

          <div>
            <button
              onClick={() => {
                setForm(() => {
                  return {
                    title: "",
                    desc: "",
                    size: "",
                    price: "",
                  };
                });
              }}
            >
              clear
            </button>
          </div>
        </div>
      </form>
      <img src={enteredImage} width="100" height="100" />
    </>
  );
};

export default AddItemForm;
