import { useState,createContext } from "react";
//import ItemList from "../component/ItemName";
export const CartContext = createContext({cartArr:[],addCartData:()=>{},DeleteItemFromProduct:()=>{}})



const CartContextProvider = (props)=>{
    const [cartArr, setCartArr] = useState([]);
    var [products, setProducts] = useState(ItemList);
    const addCartData = (item) => {
        const updatedValue = [...cartArr, item];
        setCartArr(updatedValue);
        localStorage.setItem("users", JSON.stringify(updatedValue));
        alert(` Items added To Cart`);
      };  
      
      const DeleteItemFromProduct = (Id) => {
        setProducts((prev) => {
          return prev.filter((val, index) => {
            return Id !== index;
          });
        });  
        ItemList.splice(Id,1)
      };  

      


return (
    <>
    <CartContext.Provider value={{cartArr,addCartData,DeleteItemFromProduct}}>
{props.children}
    </CartContext.Provider>
    </>
)

}

export default CartContextProvider;
