import { useState,useContext } from "react";
import { NavLink } from "react-router-dom";
import { CartContext } from "../contexts/CartContextProvider";
const Card = (props) => {
  const {addCartData} = useContext(CartContext)

  const [wishlistNameChange,setWhislistnamechange] = useState("whislist");
  var idOfProduct=props.idOfItemInArr
  return ( 
    <>
      <div className="card">
   <svg
          onClick={()=>{
            //props.addedToCart(props.item)
            addCartData(props.item)
            
          }}
          style={{ position: "absolute", right: "0px", top: "0px" }}
          xmlns="http://www.w3.org/2000/svg"
          width="25"
          height="25"
          fill="currentColor"
          className="bi bi-file-plus"
          viewBox="0 0 16 16"
        >
          <path d="M8.5 6a.5.5 0 0 0-1 0v1.5H6a.5.5 0 0 0 0 1h1.5V10a.5.5 0 0 0 1 0V8.5H10a.5.5 0 0 0 0-1H8.5V6z" />
          <path d="M2 2a2 2 0 0 1 2-2h8a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H4a2 2 0 0 1-2-2V2zm10-1H4a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h8a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1z" />
        </svg>
        {props.dltIcon === true ? (
         <button style={{ position: "absolute", left: "0px", top: "0px" }} type="button" class="btn btn-warning">
          <svg
            onClick={() => {
              props.deleteFromscreen(props.uniqProductId);
            }}
            xmlns="http://www.w3.org/2000/svg"
            width="25"
            height="25"
            
            className="bi bi-x-square"
            viewBox="0 0 16 16"
          >
            <path d="M14 1a1 1 0 0 1 1 1v12a1 1 0 0 1-1 1H2a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1h12zM2 0a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z" />
            <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z" />
          </svg>
          </button>
        ) : (
          ""
        )}
        <NavLink to={"/shop/cardDetail/"+idOfProduct} style={{textDecoration:"none",color:"black"}}>
        <div>
          <img src={props.item.image} className="card-img-top" alt="..." />

          <div className="card-body">
            <h5 className="card-title">{props.item.title}</h5>
            <h5 className="size">{props.item.availableSizes}</h5>
            <h5 className="cost">{props.item.price}</h5>
          </div>
        </div>
        </NavLink>
        {props.btndisplay!==false?<button   onClick={()=>{props.addtowishlist(props.item)
         setWhislistnamechange("Added to whislist")}} className="btn btn-primary">{wishlistNameChange}</button>:""}
      </div>
    </>  
  );
};

export default Card;
