import React, { createContext, useState, useEffect } from "react";
const Spiner = createContext();
const SpinContextProvider = ({ children }) => {
  const [spinerState, setSpiner] = useState(false);

  return (
    // the Provider gives access to the context to its children
    <Spiner.Provider value={{
        spinState:spinerState, 
        spinStateFunc:setSpiner
    }}>
      {children}
    </Spiner.Provider>
  );
};

export { Spiner, SpinContextProvider};
