import React, { createContext, useState, useEffect } from "react";
const Spiner = createContext();
const SpinContextProvider = ({ children }) => {
  const [spinerState, setSpiner] = useState(true);
  return (
    // the Provider gives access to the context to its children
    <Spiner.Provider value={spinerState}>
      {children}
    </Spiner.Provider>
  );
};

export { Spiner, SpinContextProvider};
