import React, { createContext, useState, useEffect,useContext } from "react";
import axios from 'axios'

const ProdContext = createContext();

const ProdContextProvider = ({ children }) => {
  // the value that will be given to the context
  const [product, setProduct] = useState([]);
  const [spinerState, setSpiner] = useState(false);
 // fetch a user from a fake backend API
  const spin=useContext(Spiner)
  console.log(spin)
  useEffect(() => {
    function getData(){
     axios.get('https://fakestoreapi.com/products')
        .then((response) => {
            setProduct(response.data)
            console.log(response.data)
        }).finally(()=>{
          spin.spinStateFunc(false)  
        })
      }
  getData()
  }, []);
console.log(product)
  return (
    // the Provider gives access to the context to its children
    <SpinContextProvider>
    <ProdContext.Provider value={
      {
        prod:product,
        spinState:spinerState, 
        spinStateFunc:setSpiner
      }}>
      {children}
    </ProdContext.Provider>
   </SpinContextProvider>
  );
};

export { ProdContext, ProdContextProvider };
